import React, { useEffect, useState } from 'react';
import './index.css'; // Import the CSS for this component
import Header from '../Header'; // Assuming Header is used

const Profile = () => {
    const [userData, setUserData] = useState({
        username: '',
        email: '',
        fullName: '',
        pan: '', // Placeholder for Permanent Account Number
        aadhaar: '', // Placeholder for Aadhaar Number
        address: '',
        phone: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserProfile = async () => {
            setLoading(true);
            setError('');
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication required. Please log in.');
                    setLoading(false);
                    return;
                }

                // Replace with your actual backend API endpoint to fetch user profile
                const response = await fetch('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    // Populate form with fetched data
                    setUserData({
                        username: data.user.username || '',
                        email: data.user.email || '',
                        fullName: data.user.full_name || '',
                        pan: data.user.pan || '',
                        aadhaar: data.user.aadhaar || '',
                        address: data.user.address || '',
                        phone: data.user.phone || ''
                    });
                } else {
                    setError(data.message || 'Failed to fetch user profile.');
                }
            } catch (err) {
                console.error('Error fetching profile:', err);
                setError('Network error or server is down. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSave = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('Authentication required. Please log in.');
                return;
            }

            // Replace with your actual backend API endpoint to update user profile
            const response = await fetch('http://localhost:5000/profile', {
                method: 'PUT', // or PATCH
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(userData)
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(data.message || 'Profile updated successfully!');
                setIsEditing(false); // Exit edit mode
            } else {
                setError(data.message || 'Failed to update profile. Please try again.');
            }
        } catch (err) {
            console.error('Error updating profile:', err);
            setError('Network error or server is down. Please try again later.');
        }
    };

    if (loading) {
        return (
            <div className="profile-page">
                <Header />
                <div className="profile-container loading-state">
                    <p>Loading profile...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="profile-page">
                <Header />
                <div className="profile-container error-state">
                    <p className="error-message">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="profile-page">
            <Header />
            <div className="profile-container">
                <div className="profile-header">
                    <h1>My Profile</h1>
                    <p>View and manage your account details.</p>
                </div>

                {message && <div className="status-message success">{message}</div>}
                {error && <div className="status-message error">{error}</div>}

                <form onSubmit={handleSave} className="profile-form">
                    <div className="form-group">
                        <label>Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={userData.email}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Full Name:</label>
                        <input
                            type="text"
                            name="fullName"
                            value={userData.fullName}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>PAN:</label>
                        <input
                            type="text"
                            name="pan"
                            value={userData.pan}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Aadhaar:</label>
                        <input
                            type="text"
                            name="aadhaar"
                            value={userData.aadhaar}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Address:</label>
                        <textarea
                            name="address"
                            value={userData.address}
                            onChange={handleChange}
                            disabled={!isEditing}
                            rows="3"
                        ></textarea>
                    </div>
                    <div className="form-group">
                        <label>Phone:</label>
                        <input
                            type="text"
                            name="phone"
                            value={userData.phone}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>

                    <div className="profile-actions">
                        {!isEditing ? (
                            <button type="button" onClick={() => setIsEditing(true)} className="edit-button">Edit Profile</button>
                        ) : (
                            <>
                                <button type="submit" className="save-button">Save Changes</button>
                                <button type="button" onClick={() => setIsEditing(false)} className="cancel-button">Cancel</button>
                            </>
                        )}
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Profile;
