import React from 'react';
// Correct imports for react-router-dom v5
import { BrowserRouter as Router, Switch, Route, Redirect, Link } from 'react-router-dom';
import Cookies from 'js-cookie'; // Import Cookies to check for jwt_token

// Import your components (ensure these paths are correct as per your file structure)
import Header from './components/Header'; // Assuming Header is in src/components/Header/index.js
import About from './components/About'; // Assuming About is in src/components/About/index.js
import Register from './components/Register'; // Assuming Register is in src/components/Register/index.js
import ContactUs from './components/ContactUs'; // Assuming ContactUs is in src/components/ContactUs/index.js
import Dashboard from './components/Dashboard'; // Assuming Dashboard is in src/components/Dashboard/index.js
import Profile from './components/Profile';     // Assuming Profile is in src/components/Profile/index.js
import LoginForm from './components/LoginForm'; // Correctly import LoginForm from its path

// Note: If you decide to use a dedicated ProtectedRoute component (e.g., from src/components/ProtectedRoute/index.js)
// you would uncomment the import below and use it in the <Switch> block.
// import ProtectedRoute from './components/ProtectedRoute';


const App = () => {
  // Centralized authentication check function
  const isAuthenticated = () => {
    return Cookies.get('jwt_token') !== undefined;
  };

  // Simple Home component for the root path
  const Home = () => (
    <div>
      <Header />
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h1>Welcome to Garuda AI Tax Assistant!</h1>
        <p>Your intelligent companion for effortless and optimized tax filing.</p>
        <p>
          {isAuthenticated() ? (
            // If authenticated, show link to Dashboard
            <span>You are logged in! Go to your <Link to="/dashboard">Dashboard</Link>.</span>
          ) : (
            // If not authenticated, show links to Login and Register
            <span>Please <Link to="/login">login</Link> or <Link to="/api/register">register</Link> to get started.</span>
          )}
        </p>
      </div>
    </div>
  );

  return (
    <Router>
      {/*
        The Header component can be rendered here if it should appear on ALL pages,
        regardless of the route. If it's specific to certain layouts, it should be
        rendered within those layout components or individual page components.
        For this example, it's rendered within the 'Home' component.
      */}

      <Switch> {/* Use Switch to render only the first matching Route */}
        {/* Public Routes - accessible to all users */}
        <Route exact path="/" component={Home} /> {/* Exact path for the root */}
        <Route path="/login" component={LoginForm} /> {/* Route for your LoginForm */}
        <Route path="/api/register" component={Register} /> {/* Route for RegisterForm, matches backend endpoint */}
        <Route path="/contact" component={ContactUs} />
        <Route path="/about" component={About} />

        {/* Protected Routes - only accessible if authenticated */}
        {/*
          Using the 'render' prop for inline conditional rendering is a common pattern in React Router v5
          for protected routes. It allows you to check authentication status and redirect if not authenticated.
        */}
        <Route
          path="/dashboard"
          render={(props) => (
            isAuthenticated() ? <Dashboard {...props} /> : <Redirect to="/login" />
          )}
        />
        <Route
          path="/profile"
          render={(props) => (
            isAuthenticated() ? <Profile {...props} /> : <Redirect to="/login" />
          )}
        />

        {/*
          If you prefer to use a dedicated ProtectedRoute component (e.g., if you have many protected routes
          and want to keep App.js cleaner), you would use it like this:
          <ProtectedRoute path="/dashboard" component={Dashboard} />
          <ProtectedRoute path="/profile" component={Profile} />
          (Ensure your ProtectedRoute component is correctly set up for v5, as provided previously).
        */}

        {/* Catch-all route: If no other route matches, redirect to the home page.
            This must be the very last Route/Redirect in the Switch. */}
        <Redirect to="/" />
      </Switch>
    </Router>
  );
};

export default App;
