// src/App.js
import React from 'react';
// Correct imports for react-router-dom v5
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Cookies from 'js-cookie'; // Import Cookies to check for jwt_token

// Import your components (ensure these paths are correct as per your file structure)
import Header from './components/Header'; // Assuming Header is in src/components/Header/index.js
import About from './components/About'; // Assuming About is in src/components/About/index.js
import Register from './components/Register'; // Assuming Register is in src/components/Register/index.js
import ContactUs from './components/ContactUs'; // Assuming ContactUs is in src/components/ContactUs/index.js
import Dashboard from './components/Dashboard'; // Assuming Dashboard is in src/components/Dashboard/index.js
import Profile from './components/Profile';     // Assuming Profile is in src/components/Profile/index.js
import LoginForm from './components/LoginForm'; // <--- IMPORTANT: Changed from 'Login' to 'LoginForm'
import ProtectedRoute from './components/ProtectedRoute'; // Your ProtectedRoute component
// Make sure this path is correct for your main App.css

const App = () => {
  // Authentication check function - centralize it
  const isAuthenticated = () => {
    return Cookies.get('jwt_token') !== undefined;
  };

  // Simple Home component for the root path
  const Home = (props) => ( // Home might receive history, location etc. props from Route
    <div>
      <Header />
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h1>Welcome to Garuda AI Tax Assistant!</h1>
        <p>Your intelligent companion for effortless and optimized tax filing.</p>
        <p>
          {isAuthenticated() ? (
            <span>You are logged in! Go to your <a href="/dashboard">Dashboard</a>.</span>
          ) : (
            <span>Please <a href="/login">login</a> or <a href="/register">register</a> to get started.</span>
          )}
        </p>
      </div>
    </div>
  );

  return (
    <Router>
      {/* If Header is part of the global layout that should always be present, render it here */}
      {/* <Header /> */}

      <Switch> {/* Use Switch instead of Routes */}
        {/* Public Routes */}
        <Route exact path="/" component={Home} /> {/* Use 'component' prop */}
        <Route path="/login" component={LoginForm} /> {/* <--- IMPORTANT: Use LoginForm here */}
        <Route path="/register" component={Register} />
        <Route path="/contact" component={ContactUs} />
        <Route path="/about" component={About} />

        {/* Protected Routes - only accessible if authenticated */}
        {/*
          Using `render` prop for inline conditional rendering is common in v5 for protected routes.
          It allows you to pass props explicitly and use the conditional logic.
          Alternatively, you can modify ProtectedRoute to work with `component` prop if it returns a HOC.
        */}
        <Route
          path="/dashboard"
          render={(props) => (
            isAuthenticated() ? <Dashboard {...props} /> : <Redirect to="/login" />
          )}
        />
        <Route
          path="/profile"
          render={(props) => (
            isAuthenticated() ? <Profile {...props} /> : <Redirect to="/login" />
          )}
        />

        {/* You can also define ProtectedRoute as a component wrapping a Route */}
        {/* Example:
        <ProtectedRoute path="/dashboard" component={Dashboard} />
        <ProtectedRoute path="/profile" component={Profile} />
        This would require ProtectedRoute to handle the 'component' prop correctly,
        as shown in the earlier ProtectedRoute update.
        */}


        {/* Catch-all route for any undefined paths - redirects to home */}
        {/* This must be the very last route in Switch */}
        <Redirect to="/" />
      </Switch>
    </Router>
  );
};

export default App;
