import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Cookies from 'js-cookie';
import LoginForm from './components/LoginForm'; // Corrected: Using LoginForm
import Register from './components/Register';   // Corrected: Using Register
import Dashboard from './components/Dashboard';
function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loadingAuth, setLoadingAuth] = useState(true);

  useEffect(() => {
    const token = Cookies.get('jwt_token');
    if (token) {
      setIsAuthenticated(true);
    }
    setLoadingAuth(false);
  }, []);

  const handleLogin = (token) => {
    Cookies.set('jwt_token', token, { expires: 1 }); // Token expires in 1 day
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    Cookies.remove('jwt_token');
    setIsAuthenticated(false);
  };

  if (loadingAuth) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-xl font-semibold text-gray-700">Loading authentication...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Switch>
          {/* Route for Login */}
          <Route 
            path="/login" 
            render={() => (isAuthenticated ? <Redirect to="/dashboard" /> : <LoginForm onLogin={handleLogin} />)} 
          />
          {/* Route for Register */}
          <Route 
            path="/register" 
            render={() => (isAuthenticated ? <Redirect to="/dashboard" /> : <Register />)} 
          />
          
          {/* Protected Dashboard Route */}
          <Route 
            path="/dashboard"
            render={() => (isAuthenticated ? <Dashboard onLogout={handleLogout} /> : <Redirect to="/login" />)} 
          />

          {/* Default route: if authenticated, go to dashboard, otherwise to login */}
          <Route 
            exact 
            path="/" 
            render={() => (isAuthenticated ? <Redirect to="/dashboard" /> : <Redirect to="/login" />)} 
          />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
