// src/components/Profile/index.js
import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie'; // <--- ADD THIS IMPORT
// If you need programmatic navigation inside this component (e.g., after an action),
// you would use useHistory hook from react-router-dom v5:
// import { useHistory } from 'react-router-dom';
import './index.css';
import Header from '../Header';

const Profile = () => {
    // const history = useHistory(); // Uncomment if you need to use it

    const [userData, setUserData] = useState({
        username: '',
        email: '',
        fullName: '',
        pan: '',
        aadhaar: '',
        address: '',
        phone: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserProfile = async () => {
            setLoading(true);
            setError('');
            try {
                // CHANGE: Retrieve token from Cookies with the correct key
                const token = Cookies.get('jwt_token');
                if (!token) {
                    setError('Authentication required. Please log in.');
                    // history.push('/login'); // Example: Redirect if no token
                    setLoading(false);
                    return;
                }

                // Make sure your backend route for /profile matches this path.
                const response = await fetch('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    setUserData({
                        username: data.user.username || '',
                        email: data.user.email || '',
                        fullName: data.user.full_name || '',
                        pan: data.user.pan || '',
                        aadhaar: data.user.aadhaar || '',
                        address: data.user.address || '',
                        phone: data.user.phone || ''
                    });
                } else {
                    // Improved error handling
                    let errorMessage = 'Failed to fetch user profile.';
                    if (response.status === 401) {
                        errorMessage = "Session expired or unauthorized. Please log in again.";
                        Cookies.remove('jwt_token'); // Clear invalid token
                    } else {
                        errorMessage = data.message || errorMessage;
                    }
                    setError(errorMessage);
                }
            } catch (err) {
                console.error('Error fetching profile:', err);
                setError('Network error or server is down. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSave = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            // CHANGE: Retrieve token from Cookies with the correct key
            const token = Cookies.get('jwt_token');
            if (!token) {
                setError('Authentication required. Please log in.');
                return;
            }

            const response = await fetch('http://localhost:5000/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(userData)
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(data.message || 'Profile updated successfully!');
                setIsEditing(false);
            } else {
                // Improved error handling
                let errorMessage = 'Failed to update profile. Please try again.';
                if (response.status === 401) {
                    errorMessage = "Session expired or unauthorized. Please log in again.";
                    Cookies.remove('jwt_token'); // Clear invalid token
                } else {
                    errorMessage = data.message || errorMessage;
                }
                setError(errorMessage);
            }
        } catch (err) {
            console.error('Error updating profile:', err);
            setError('Network error or server is down. Please try again later.');
        }
    };

    if (loading) {
        return (
            <div className="profile-page">
                <Header />
                <div className="profile-container loading-state">
                    <p>Loading profile...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="profile-page">
                <Header />
                <div className="profile-container error-state">
                    <p className="error-message">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="profile-page">
            <Header />
            <div className="profile-container">
                <div className="profile-header">
                    <h1>My Profile</h1>
                    <p>View and manage your account details.</p>
                </div>

                {message && <div className="status-message success">{message}</div>}
                {error && <div className="status-message error">{error}</div>}

                <form onSubmit={handleSave} className="profile-form">
                    <div className="form-group">
                        <label>Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={userData.email}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Full Name:</label>
                        <input
                            type="text"
                            name="fullName"
                            value={userData.fullName}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>PAN:</label>
                        <input
                            type="text"
                            name="pan"
                            value={userData.pan}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Aadhaar:</label>
                        <input
                            type="text"
                            name="aadhaar"
                            value={userData.aadhaar}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="form-group">
                        <label>Address:</label>
                        <textarea
                            name="address"
                            value={userData.address}
                            onChange={handleChange}
                            disabled={!isEditing}
                            rows="3"
                        ></textarea>
                    </div>
                    <div className="form-group">
                        <label>Phone:</label>
                        <input
                            type="text"
                            name="phone"
                            value={userData.phone}
                            onChange={handleChange}
                            disabled={!isEditing}
                        />
                    </div>

                    <div className="profile-actions">
                        {!isEditing ? (
                            <button type="button" onClick={() => setIsEditing(true)} className="edit-button">Edit Profile</button>
                        ) : (
                            <>
                                <button type="submit" className="save-button">Save Changes</button>
                                <button type="button" onClick={() => setIsEditing(false)} className="cancel-button">Cancel</button>
                            </>
                        )}
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Profile;
