// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Import your components
import Header from './components/Header';
import About from './components/About';
import Register from './components/Register';
import ContactUs from './components/ContactUs';
import Dashboard from './components/Dashboard'; // New Import
import Profile from './components/Profile';     // New Import
import ProtectedRoute from './components/ProtectedRoute'; // Your ProtectedRoute component
import LoginForm from './components/LoginForm'; // Assuming you have a Login component

import './App.css'; // Make sure this path is correct for your main App.css

const App = () => {
  // This is a placeholder for your actual authentication logic.
  // In a real app, you would check if the user is logged in (e.g., by checking for a token).
  const isAuthenticated = localStorage.getItem('token') ? true : false;

  // Simple Home component for the root path
  const Home = () => (
    <div>
      <Header />
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h1>Welcome to Garuda AI Tax Assistant!</h1>
        <p>Your intelligent companion for effortless and optimized tax filing.</p>
        <p>Please <a href="/login">login</a> or <a href="/register">register</a> to get started.</p>
      </div>
    </div>
  );

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<LoginForm />} />
        <Route path="/register" element={<Register />} />
        <Route path="/contact" element={<ContactUs />} />
        <Route path="/about" element={<About />} />

        {/* Protected Routes - only accessible if authenticated */}
        {/* You can use the `element` prop with conditional rendering directly */}
        <Route
          path="/dashboard"
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/profile"
          element={isAuthenticated ? <Profile /> : <Navigate to="/login" replace />}
        />

        {/*
        // Alternatively, if your ProtectedRoute component handles the redirection internally:
        <Route path="/dashboard" element={<ProtectedRoute component={Dashboard} />} />
        <Route path="/profile" element={<ProtectedRoute component={Profile} />} />
        */}

        {/* Catch-all route for any undefined paths - redirects to home */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
