import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

// Inline CSS for the TaxUploader component to address import issues and ensure consistency
const TaxUploader = () => {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState(''); // 'info', 'success', 'error'
    const [uploadResult, setUploadResult] = useState(null); // Stores initial processing result (record_id, doc summary)
    const [suggestionsResult, setSuggestionsResult] = useState(null); // Stores suggestions and ML predictions
    const [isGeneratingSuggestions, setIsGeneratingSuggestions] = useState(false);
    const [isGeneratingITR, setIsGeneratingITR] = useState(false);
    // New state for document type hint
    const [documentTypeHint, setDocumentTypeHint] = useState('Auto-Detect'); // Default value

    // List of common document types for the dropdown
    const documentTypes = [
        'Auto-Detect', // Default option, AI attempts to detect
        'Form 16',
        'Bank Statement',
        'Form 26AS',
        'Salary Slip',
        'Investment Proof',
        'Home Loan Statement',
        'Other Document',
    ];


    // Helper function to safely format currency values
    const formatCurrency = (value) => {
        if (value === null || value === undefined || isNaN(parseFloat(value))) {
            return 'N/A';
        }
        return parseFloat(value).toLocaleString('en-IN', {
            style: 'currency',
            currency: 'INR',
            minimumFractionDigits: 2,
            maximumFractionDigits: 2,
        });
    };

    // The formatNumber function was not being used and has been removed to clear the ESLint warning.
    // const formatNumber = (value) => {
    //     if (value === null || value === undefined || isNaN(parseFloat(value))) {
    //         return 'N/A';
    //     }
    //     return parseFloat(value).toLocaleString('en-IN');
    // };

    const handleFileChange = (event) => {
        // Convert FileList to Array and update state
        setSelectedFiles(Array.from(event.target.files));
        setMessage('');
        setMessageType('');
        setUploadResult(null);
        setSuggestionsResult(null);
    };

    const handleDocumentTypeChange = (event) => {
        setDocumentTypeHint(event.target.value);
    };

    const handleUpload = async () => {
        if (selectedFiles.length === 0) {
            setMessage('Please select at least one file to upload.');
            setMessageType('error');
            return;
        }

        setLoading(true);
        setMessage('Uploading and processing documents...');
        setMessageType('info');
        setUploadResult(null);
        setSuggestionsResult(null); // Clear previous results

        const formData = new FormData();
        selectedFiles.forEach(file => {
            formData.append('documents', file);
        });
        formData.append('document_type', documentTypeHint); // Append the document type hint

        const jwt_token = Cookies.get('jwt_token');

        try {
            const response = await axios.post('http://localhost:5000/api/process_documents', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${jwt_token}`
                },
                withCredentials: true,
            });

            if (response.data.status === 'success') {
                setMessage('Documents processed successfully!');
                setMessageType('success');
                setUploadResult(response.data);
                // No automatic suggestion generation here, user clicks button
            } else {
                setMessage(response.data.message || 'Failed to process documents.');
                setMessageType('error');
            }
        } catch (error) {
            console.error('Error during document upload:', error);
            setMessage(error.response?.data?.message || 'An error occurred during upload.');
            setMessageType('error');
        } finally {
            setLoading(false);
        }
    };

    const handleGetSuggestions = async () => {
        if (!uploadResult || !uploadResult.record_id) {
            setMessage('Please upload and process documents first to get suggestions.');
            setMessageType('error');
            return;
        }

        setIsGeneratingSuggestions(true);
        setMessage('Generating AI suggestions and ML predictions...');
        setMessageType('info');
        setSuggestionsResult(null); // Clear previous suggestions

        const jwt_token = Cookies.get('jwt_token');

        try {
            const response = await axios.post('http://localhost:5000/api/get_suggestions', 
                { record_id: uploadResult.record_id }, 
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${jwt_token}`
                    },
                    withCredentials: true,
                }
            );

            if (response.data.status === 'success') {
                setMessage('Suggestions and predictions generated!');
                setMessageType('success');
                setSuggestionsResult(response.data);
            } else {
                setMessage(response.data.message || 'Failed to get suggestions.');
                setMessageType('error');
                setSuggestionsResult(response.data); // Still set results to show any partial data/errors
            }
        } catch (error) {
            console.error('Error getting suggestions:', error);
            setMessage(error.response?.data?.message || 'An error occurred while fetching suggestions.');
            setMessageType('error');
        } finally {
            setIsGeneratingSuggestions(false);
        }
    };

    const handleGenerateITR = async () => {
        if (!uploadResult || !uploadResult.record_id) {
            setMessage('Please process documents and get suggestions first to generate ITR.');
            setMessageType('error');
            return;
        }

        setIsGeneratingITR(true);
        setMessage('Generating ITR form...');
        setMessageType('info');

        const jwt_token = Cookies.get('jwt_token');

        try {
            // Adjust the URL to match the backend route for ITR generation
            const response = await axios.get(`http://localhost:5000/api/generate-itr/${uploadResult.record_id}`, {
                headers: {
                    'Authorization': `Bearer ${jwt_token}`
                },
                responseType: 'blob', // Important: receive the response as a Blob
                withCredentials: true,
            });

            if (response.status === 200) {
                // Create a blob from the response data
                const blob = new Blob([response.data], { type: 'application/pdf' });
                // Create a URL for the blob
                const url = window.URL.createObjectURL(blob);
                // Get filename from Content-Disposition header if available
                const contentDisposition = response.headers['content-disposition'];
                let filename = 'ITR_Form.pdf'; // Default filename
                if (contentDisposition) {
                    const filenameMatch = contentDisposition.match(/filename="(.+)"/);
                    if (filenameMatch && filenameMatch[1]) {
                        filename = filenameMatch[1];
                    }
                }
                // Create a link element, set its href and download attributes, and click it
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', filename);
                document.body.appendChild(link);
                link.click();
                link.parentNode.removeChild(link);
                window.URL.revokeObjectURL(url); // Clean up the URL object

                setMessage('ITR form generated and downloaded!');
                setMessageType('success');
            } else {
                // If it's not a PDF but an error JSON, try to parse it
                const errorData = await new Response(response.data).json();
                setMessage(errorData.message || 'Failed to generate ITR form.');
                setMessageType('error');
            }
        } catch (error) {
            console.error('Error generating ITR:', error);
            let errorMessage = 'An error occurred while generating ITR.';
            if (error.response && error.response.data) {
                // Try to read the error message from the blob response
                const reader = new FileReader();
                reader.onload = function() {
                    try {
                        const errorJson = JSON.parse(reader.result);
                        errorMessage = errorJson.message || errorMessage;
                    } catch (e) {
                        // Not a JSON error, use generic message
                    } finally {
                        setMessage(errorMessage);
                        setMessageType('error');
                    }
                };
                reader.readAsText(error.response.data); // Read the blob as text
            } else {
                setMessage(errorMessage);
                setMessageType('error');
            }
        } finally {
            setIsGeneratingITR(false);
        }
    };


    return (
        <div className="p-6 max-w-7xl mx-auto bg-gray-100 min-h-screen font-sans">
            <style>
                {`
                @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');

                .font-sans {
                    font-family: 'Inter', sans-serif;
                }
                .container-card {
                    background-color: #ffffff;
                    border-radius: 0.75rem; /* rounded-xl */
                    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* shadow-lg */
                    padding: 1.5rem; /* p-6 */
                    margin-bottom: 1.5rem; /* mb-6 */
                }
                .section-box {
                    background-color: #f9fafb; /* bg-gray-50 */
                    border-radius: 0.5rem; /* rounded-lg */
                    padding: 1.25rem; /* p-5 */
                    margin-top: 1rem; /* mt-4 */
                    border: 1px solid #e5e7eb; /* border border-gray-200 */
                }
                .btn-primary {
                    background-color: #4f46e5; /* indigo-600 */
                    color: white;
                    padding: 0.75rem 1.5rem; /* px-6 py-3 */
                    border-radius: 0.5rem; /* rounded-lg */
                    font-weight: 600; /* font-semibold */
                    transition: background-color 0.3s ease;
                }
                .btn-primary:hover {
                    background-color: #4338ca; /* indigo-700 */
                }
                .btn-secondary {
                    background-color: #6b7280; /* gray-500 */
                    color: white;
                    padding: 0.75rem 1.5rem;
                    border-radius: 0.5rem;
                    font-weight: 600;
                    transition: background-color 0.3s ease;
                }
                .btn-secondary:hover {
                    background-color: #4b5563; /* gray-600 */
                }
                .message {
                    padding: 0.75rem 1.25rem;
                    border-radius: 0.5rem;
                    margin-top: 1rem;
                    font-weight: 500;
                    display: flex;
                    align-items: center;
                }
                .message.info {
                    background-color: #e0f2fe; /* light blue */
                    color: #0284c7; /* sky-600 */
                }
                .message.success {
                    background-color: #dcfce7; /* light green */
                    color: #22c55e; /* green-500 */
                }
                .message.error {
                    background-color: #fee2e2; /* light red */
                    color: #ef4444; /* red-500 */
                }
                .message svg {
                    margin-right: 0.5rem;
                }
                .loading-spinner {
                    border: 4px solid rgba(255, 255, 255, 0.3);
                    border-radius: 50%;
                    border-top: 4px solid #fff;
                    width: 24px;
                    height: 24px;
                    animation: spin 1s linear infinite;
                    margin-right: 0.5rem;
                }
                @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                }
                .refund-amount {
                    color: #22c55e; /* green-500 */
                    font-weight: 600;
                }
                .tax-due-amount {
                    color: #ef4444; /* red-500 */
                    font-weight: 600;
                }

                /* Responsive adjustments */
                @media (max-width: 768px) {
                    .flex-col-mobile {
                        flex-direction: column;
                        align-items: stretch;
                    }
                    .flex-col-mobile .btn-primary,
                    .flex-col-mobile .btn-secondary {
                        width: 100%;
                        margin-top: 0.5rem;
                        margin-left: 0 !important;
                        margin-right: 0 !important;
                    }
                }
                `}
            </style>
            <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Document Uploader & Tax AI</h2>

            <div className="container-card">
                <h3 className="text-xl font-semibold text-gray-700 mb-4">Upload Documents</h3>
                
                <div className="mb-4">
                    <label htmlFor="documentType" className="block text-gray-700 text-sm font-bold mb-2">
                        Hint Document Type (Optional, helps AI):
                    </label>
                    <select
                        id="documentType"
                        name="documentType"
                        value={documentTypeHint}
                        onChange={handleDocumentTypeChange}
                        className="shadow border rounded-lg w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline bg-white"
                    >
                        {documentTypes.map((type) => (
                            <option key={type} value={type}>
                                {type}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="mb-4">
                    <label htmlFor="document-upload" className="block text-gray-700 text-sm font-bold mb-2">
                        Select one or more documents (PDF, JPG, PNG):
                    </label>
                    <input
                        type="file"
                        id="document-upload"
                        multiple
                        onChange={handleFileChange}
                        className="block w-full text-sm text-gray-500
                                   file:mr-4 file:py-2 file:px-4
                                   file:rounded-md file:border-0
                                   file:text-sm file:font-semibold
                                   file:bg-indigo-50 file:text-indigo-700
                                   hover:file:bg-indigo-100"
                    />
                </div>
                
                {selectedFiles.length > 0 && (
                    <div className="mb-4 text-gray-600 text-sm">
                        <p>Selected Files:</p>
                        <ul className="list-disc list-inside">
                            {selectedFiles.map((file, index) => (
                                <li key={index}>{file.name}</li>
                            ))}
                        </ul>
                    </div>
                )}

                <button
                    onClick={handleUpload}
                    disabled={loading}
                    className={`btn-primary flex items-center justify-center ${loading ? 'opacity-75 cursor-not-allowed' : ''}`}
                >
                    {loading ? (
                        <>
                            <span className="loading-spinner"></span>
                            Uploading...
                        </>
                    ) : (
                        'Upload & Process Documents'
                    )}
                </button>

                {message && (
                    <div className={`message ${messageType} mt-4`}>
                        {messageType === 'info' && <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>}
                        {messageType === 'success' && <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>}
                        {messageType === 'error' && <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>}
                        {message}
                    </div>
                )}
            </div>

            {uploadResult && (
                <div className="container-card">
                    <h3 className="text-xl font-semibold text-gray-700 mb-4">Document Processing Result</h3>
                    <p className="text-gray-600 mb-2">Record ID: <strong>{uploadResult.record_id}</strong></p>
                    
                    {uploadResult.document_processing_summary && uploadResult.document_processing_summary.length > 0 && (
                        <div className="section-box">
                            <h4 className="text-lg font-medium text-gray-700 mb-2">Summary of Each Document:</h4>
                            {uploadResult.document_processing_summary.map((doc, index) => (
                                <div key={index} className={`mb-4 p-3 rounded-md ${doc.status === 'success' ? 'bg-green-50 text-green-800' : doc.status === 'warning' ? 'bg-yellow-50 text-yellow-800' : 'bg-red-50 text-red-800'}`}>
                                    <p><strong>File:</strong> {doc.filename} {doc.stored_path && <a href={`http://localhost:5000${doc.stored_path}`} target="_blank" rel="noopener noreferrer" className="text-indigo-600 hover:underline">(View Stored Document)</a>}</p>
                                    <p><strong>Status:</strong> <span className="uppercase">{doc.status}</span></p>
                                    <p><strong>Identified Type:</strong> {doc.identified_type}</p>
                                    <p><strong>Message:</strong> {doc.message}</p>
                                    {doc.extracted_fields && (
                                        <div className="mt-2 text-sm">
                                            <strong>Extracted Fields:</strong>
                                            <ul className="list-disc list-inside ml-4">
                                                {Object.entries(doc.extracted_fields).map(([key, value]) => (
                                                    // Only show non-null/non-zero values for brevity
                                                    (value !== null && value !== 'null' && value !== 0 && value !== 0.0 && (Array.isArray(value) ? value.length > 0 : true)) && (
                                                        <li key={key}>
                                                            <strong>{key.replace(/_/g, ' ').replace(/\b\w/g, c => c.toUpperCase())}:</strong> {
                                                                typeof value === 'number' ? formatCurrency(value) : 
                                                                (Array.isArray(value) ? (key === 'transaction_summary' ? `${value.length} transactions` : JSON.stringify(value)) : String(value))
                                                            }
                                                        </li>
                                                    )
                                                ))}
                                            </ul>
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    )}

                    <div className="section-box">
                        <h3 className="text-lg font-medium text-gray-700 mb-2">Aggregated & Rule-Based Computation</h3>
                        {uploadResult.aggregated_financial_data && (
                            <div className="mt-4">
                                <h4 className="font-semibold text-gray-800">Aggregated Financial Data Summary</h4>
                                <ul className="list-disc list-inside ml-4 text-gray-700">
                                    <li><strong>Financial Year:</strong> {uploadResult.aggregated_financial_data.financial_year || 'N/A'}</li>
                                    <li><strong>Assessment Year:</strong> {uploadResult.aggregated_financial_data.assessment_year || 'N/A'}</li>
                                    <li><strong>Name:</strong> {uploadResult.aggregated_financial_data.name_of_employee || 'N/A'}</li>
                                    <li><strong>PAN:</strong> {uploadResult.aggregated_financial_data.pan_of_employee || 'N/A'}</li>
                                    <li><strong>Date of Birth:</strong> {uploadResult.aggregated_financial_data.date_of_birth || 'N/A'}</li>
                                    <li><strong>Age:</strong> {uploadResult.aggregated_financial_data.Age || 'N/A'}</li>
                                </ul>

                                <h4 className="font-semibold text-gray-800 mt-4">Income Details</h4>
                                <ul className="list-disc list-inside ml-4 text-gray-700">
                                    <li><strong>Gross Annual Salary:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_gross_salary)}</li>
                                    <li><strong>Exempt Allowances:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_exempt_allowances)}</li>
                                    <li><strong>Income from House Property:</strong> {formatCurrency(uploadResult.aggregated_financial_data.income_from_house_property)}</li>
                                    <li><strong>Income from Other Sources:</strong> {formatCurrency(uploadResult.aggregated_financial_data.income_from_other_sources)}</li>
                                    <li><strong>Long Term Capital Gains:</strong> {formatCurrency(uploadResult.aggregated_financial_data.capital_gains_long_term)}</li>
                                    <li><strong>Short Term Capital Gains:</strong> {formatCurrency(uploadResult.aggregated_financial_data.capital_gains_short_term)}</li>
                                    <li><strong>Total Gross Income (Aggregated):</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_gross_income)}</li>
                                </ul>

                                <h4 className="font-semibold text-gray-800 mt-4">Deductions Claimed</h4>
                                <ul className="list-disc list-inside ml-4 text-gray-700">
                                    <li><strong>Standard Deduction:</strong> {formatCurrency(uploadResult.aggregated_financial_data.standard_deduction)}</li>
                                    <li><strong>Professional Tax:</strong> {formatCurrency(uploadResult.aggregated_financial_data.professional_tax)}</li>
                                    <li><strong>Interest on Home Loan (Section 24(b)):</strong> {formatCurrency(uploadResult.aggregated_financial_data.interest_on_housing_loan_24b)}</li>
                                    <li><strong>Section 80C:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80C)}</li>
                                    <li><strong>Section 80D:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80D)}</li>
                                    <li><strong>Section 80E:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80E)}</li>
                                    <li><strong>Section 80G:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80G)}</li>
                                    <li><strong>Section 80CCD(1B):</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80CCD1B)}</li>
                                    <li><strong>Section 80TTA:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80TTA)}</li>
                                    <li><strong>Section 80TTB:</strong> {formatCurrency(uploadResult.aggregated_financial_data.deduction_80TTB)}</li>
                                    <li><strong>Total Chapter VI-A Deductions:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_deductions_chapter_via)}</li>
                                    <li><strong>Total Deductions (Aggregated for Display):</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_deductions)}</li>
                                </ul>

                                <h4 className="font-semibold text-gray-800 mt-4">Tax Payments & Regime</h4>
                                <ul className="list-disc list-inside ml-4 text-gray-700">
                                    <li><strong>Total TDS Credit:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_tds)}</li>
                                    <li><strong>Advance Tax Paid:</strong> {formatCurrency(uploadResult.aggregated_financial_data.advance_tax)}</li>
                                    <li><strong>Self-Assessment Tax Paid:</strong> {formatCurrency(uploadResult.aggregated_financial_data.self_assessment_tax)}</li>
                                    <li><strong>Tax Regime Chosen (from docs):</strong> {uploadResult.aggregated_financial_data.tax_regime_chosen || 'null'}</li>
                                </ul>

                                {/* Display Bank Statement Specific Details if available */}
                                {(uploadResult.aggregated_financial_data.identified_type === 'Bank Statement' || (uploadResult.aggregated_financial_data.account_number && uploadResult.aggregated_financial_data.account_number !== 'null')) && (
                                    <div className="mt-4">
                                        <h4 className="font-semibold text-gray-800">Bank Statement Details</h4>
                                        <ul className="list-disc list-inside ml-4 text-gray-700">
                                            <li><strong>Account Holder Name:</strong> {uploadResult.aggregated_financial_data.account_holder_name || 'N/A'}</li>
                                            <li><strong>Account Number:</strong> {uploadResult.aggregated_financial_data.account_number || 'N/A'}</li>
                                            <li><strong>Bank Name:</strong> {uploadResult.aggregated_financial_data.bank_name || 'N/A'}</li>
                                            <li><strong>Branch Address:</strong> {uploadResult.aggregated_financial_data.branch_address || 'N/A'}</li>
                                            <li><strong>Statement Period:</strong> {uploadResult.aggregated_financial_data.statement_start_date || 'N/A'} to {uploadResult.aggregated_financial_data.statement_end_date || 'N/A'}</li>
                                            <li><strong>Opening Balance:</strong> {formatCurrency(uploadResult.aggregated_financial_data.opening_balance)}</li>
                                            <li><strong>Closing Balance:</strong> {formatCurrency(uploadResult.aggregated_financial_data.closing_balance)}</li>
                                            <li><strong>Total Deposits:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_deposits)}</li>
                                            <li><strong>Total Withdrawals:</strong> {formatCurrency(uploadResult.aggregated_financial_data.total_withdrawals)}</li>
                                            {uploadResult.aggregated_financial_data.transaction_summary && uploadResult.aggregated_financial_data.transaction_summary.length > 0 && (
                                                <li><strong>Key Transactions:</strong> {uploadResult.aggregated_financial_data.transaction_summary.slice(0, 3).map(tx => `${tx.date}: ${tx.description} ${formatCurrency(tx.amount)}`).join('; ')}{uploadResult.aggregated_financial_data.transaction_summary.length > 3 ? '...' : ''}</li>
                                            )}
                                        </ul>
                                    </div>
                                )}

                                <h4 className="font-semibold text-gray-800 mt-4">Final Tax Computation Summary (Rule-Based)</h4>
                                <ul className="list-disc list-inside ml-4 text-gray-700">
                                    {uploadResult.final_tax_computation_summary?.calculation_details?.map((detail, idx) => (
                                        <li key={idx} className="text-sm">{detail}</li>
                                    ))}
                                </ul>
                                <p className="mt-2"><strong>Computed Taxable Income:</strong> {formatCurrency(uploadResult.final_tax_computation_summary.computed_taxable_income)}</p>
                                <p><strong>Estimated Tax Payable:</strong> {formatCurrency(uploadResult.final_tax_computation_summary.estimated_tax_payable)}</p>
                                <p><strong>Tax Regime Considered for Rule-Based Calculation:</strong> {uploadResult.final_tax_computation_summary.predicted_tax_regime}</p>
                                {uploadResult.final_tax_computation_summary.notes && uploadResult.final_tax_computation_summary.notes.length > 0 && (
                                    <p className="mt-2"><strong>Note:</strong> {uploadResult.final_tax_computation_summary.notes.join(' ')}</p>
                                )}
                            </div>
                        )}
                    </div>
                    
                    <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 mt-6">
                        <button
                            onClick={handleGetSuggestions}
                            disabled={isGeneratingSuggestions}
                            className={`btn-primary flex items-center justify-center ${isGeneratingSuggestions ? 'opacity-75 cursor-not-allowed' : ''}`}
                        >
                            {isGeneratingSuggestions ? (
                                <>
                                    <span className="loading-spinner"></span>
                                    Generating AI Suggestions...
                                </>
                            ) : (
                                'Get AI Suggestions & ML Predictions'
                            )}
                        </button>
                        <button
                            onClick={handleGenerateITR}
                            disabled={isGeneratingITR}
                            className={`btn-secondary flex items-center justify-center ${isGeneratingITR ? 'opacity-75 cursor-not-allowed' : ''}`}
                        >
                            {isGeneratingITR ? (
                                <>
                                    <span className="loading-spinner"></span>
                                    Generating ITR Form...
                                </>
                            ) : (
                                'Generate Dummy ITR Form (PDF)'
                            )}
                        </button>
                    </div>
                </div>
            )}

            {suggestionsResult && (
                <div className="container-card">
                    <h3 className="text-xl font-semibold text-gray-700 mb-4">AI-Powered Suggestions & ML Predictions</h3>
                    <div className="section-box">
                        {suggestionsResult.suggestions_from_gemini && suggestionsResult.suggestions_from_gemini.length > 0 && (
                            <div className="mb-4">
                                <h4>Tax-Saving Suggestions:</h4>
                                <ul className="list-disc list-inside ml-4">
                                    {suggestionsResult.suggestions_from_gemini.map((suggestion, index) => (
                                        <li key={index}>{suggestion}</li>
                                    ))}
                                </ul>
                            </div>
                        )}

                        {suggestionsResult.gemini_regime_analysis && suggestionsResult.gemini_regime_analysis !== 'null' && (
                            <div className="mb-4">
                                <h4>Tax Regime Analysis:</h4>
                                <p>{suggestionsResult.gemini_regime_analysis}</p>
                            </div>
                        )}

                        {suggestionsResult.ml_prediction_summary && (
                            <>
                                <h4>ML Model Prediction:</h4>
                                {suggestionsResult.ml_prediction_summary.predicted_tax_regime && (
                                    <p><strong>Predicted Tax Regime:</strong> {suggestionsResult.ml_prediction_summary.predicted_tax_regime}</p>
                                )}
                                <p><strong>Predicted Tax Liability:</strong> {formatCurrency(suggestionsResult.ml_prediction_summary.predicted_tax_liability)}</p>
                                <p className="refund-amount"><strong>Predicted Refund Due:</strong> {formatCurrency(suggestionsResult.ml_prediction_summary.predicted_refund_due)}</p>
                                <p className="tax-due-amount"><strong>Predicted Additional Tax Due:</strong> {formatCurrency(suggestionsResult.ml_prediction_summary.predicted_additional_due)}</p>
                                {suggestionsResult.ml_prediction_summary.notes && (
                                    <p><strong>Notes:</strong> {suggestionsResult.ml_prediction_summary.notes}</p>
                                )}
                            </>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};
export default TaxUploader;
