// src/App.js
import React from 'react';
// Correct imports for react-router-dom v5
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';

// Import your components (ensure these paths are correct as per your file structure)
import Header from './components/Header';
import About from './components/About';
import Register from './components/Register';
import ContactUs from './components/ContactUs';
import Dashboard from './components/Dashboard';
import Profile from './components/Profile';
import ProtectedRoute from './components/ProtectedRoute';
import LoginForm from './components/Login'; // Assuming you have a Login component

import './App.css'; // Make sure this path is correct for your main App.css

const App = () => {
  // This is a placeholder for your actual authentication logic.
  // In a real app, you would check if the user is logged in (e.g., by checking for a token).
  // For demonstration, let's assume a simple check.
  const isAuthenticated = localStorage.getItem('token') ? true : false;

  // Simple Home component for the root path
  const Home = () => (
    <div>
      <Header />
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h1>Welcome to Garuda AI Tax Assistant!</h1>
        <p>Your intelligent companion for effortless and optimized tax filing.</p>
        <p>Please <a href="/login">login</a> or <a href="/register">register</a> to get started.</p>
      </div>
    </div>
  );

  return (
    <Router>
      {/* If Header is part of the global layout, render it outside <Switch> */}
      {/* <Header /> */}

      <Switch> {/* Use Switch instead of Routes */}
        {/* Public Routes */}
        <Route exact path="/" component={Home} /> {/* Use 'component' prop */}
        <Route path="/login" component={Login} />
        <Route path="/register" component={Register} />
        <Route path="/contact" component={ContactUs} />
        <Route path="/about" component={About} />

        {/* Protected Routes - only accessible if authenticated */}
        {/* Option 1: Inline conditional Redirect */}
        <Route path="/dashboard" render={(props) => (
          isAuthenticated ? <Dashboard {...props} /> : <Redirect to="/login" />
        )} />
        <Route path="/profile" render={(props) => (
          isAuthenticated ? <Profile {...props} /> : <Redirect to="/login" />
        )} />

        {/* Option 2: Using the ProtectedRoute component */}
        {/* <Route path="/dashboard" component={ProtectedRoute(Dashboard)} /> */}
        {/* <Route path="/profile" component={ProtectedRoute(Profile)} /> */}

        {/* Catch-all route for any undefined paths - redirects to home */}
        {/* This should be the last route in Switch */}
        <Redirect to="/" />
      </Switch>
    </Router>
  );
};

export default App;
