// src/components/Dashboard/index.js
import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
// Import Link for internal navigation
import { Link } from 'react-router-dom'; 
// If you need programmatic navigation inside this component (e.g., after an action),
// you would use useHistory hook from react-router-dom v5:
// import { useHistory } from 'react-router-dom';
import './index.css';
import Header from '../Header';

const Dashboard = () => {
    // const history = useHistory(); // Uncomment if you need to use it

    const [userData, setUserData] = useState(null);
    const [taxRecords, setTaxRecords] = useState([]); // Initial state is an empty array
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchDashboardData = async () => {
            setLoading(true);
            setError('');
            try {
                const token = Cookies.get('jwt_token'); 

                if (!token) {
                    setError('Authentication required. Please log in.');
                    // If you wanted to redirect from here, you would use history.push('/login');
                    // history.push('/login'); // Example: Redirect if no token
                    setLoading(false);
                    return;
                }

                // Fetch user profile data
                const userResponse = await fetch('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const userData = await userResponse.json();

                // Fetch tax records data
                const taxRecordsResponse = await fetch('http://localhost:5000/tax-records', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const taxRecordsData = await taxRecordsResponse.json();

                if (userResponse.ok && taxRecordsResponse.ok) {
                    setUserData(userData.user);
                    // CORRECTED: Access 'history' property from taxRecordsData
                    setTaxRecords(taxRecordsData.history); 
                } else {
                    let errorMessage = 'Failed to fetch dashboard data.';
                    if (userResponse.status === 401 || taxRecordsResponse.status === 401) {
                         errorMessage = "Session expired or unauthorized. Please log in again.";
                         Cookies.remove('jwt_token'); // Clear invalid token
                         // You might also want to redirect to login here: history.push('/login');
                    } else {
                        // Use messages from backend if available, otherwise generic error
                        errorMessage = userData.message || taxRecordsData.message || errorMessage;
                    }
                    setError(errorMessage);
                }
            } catch (err) {
                console.error('Error fetching dashboard data:', err);
                setError('Network error or server is down. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    if (loading) {
        return (
            <div className="dashboard-page">
                <Header />
                <div className="dashboard-container loading-state">
                    <p>Loading dashboard data...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="dashboard-page">
                <Header />
                <div className="dashboard-container error-state">
                    <p className="error-message">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-page">
            <Header />
            <div className="dashboard-container">
                <div className="dashboard-header">
                    <h1>Welcome, {userData?.username || 'User'}!</h1>
                    <p>Your personalized tax assistant dashboard.</p>
                </div>

                <div className="dashboard-card-section">
                    <div className="dashboard-card">
                        <h3>Tax Filing Status</h3>
                        <p>Your current tax filing status for 2024-25: <span className="status-pending">Pending</span></p>
                        <button className="dashboard-action-button">Start New Filing</button>
                    </div>

                    <div className="dashboard-card">
                        <h3>Recent Activity</h3>
                        {/* taxRecords is guaranteed to be an array due to useState([]) and the fix above */}
                        {taxRecords.length > 0 ? (
                            <ul>
                                {taxRecords.slice(0, 3).map((record, index) => ( // Show last 3 records
                                    <li key={record._id || index}>
                                        ITR Type: {record.itr_type || 'N/A'} - Status: {record.status || 'N/A'}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No recent tax records found.</p>
                        )}
                        <button className="dashboard-action-button secondary">View All Records</button>
                    </div>

                    <div className="dashboard-card">
                        <h3>Quick Actions</h3>
                        <ul>
                            {/* Changed <a> tags to <Link> components */}
                            <li><Link to="/upload-documents">Upload Documents</Link></li>
                            <li><Link to="/tax-calculator">Tax Calculator</Link></li>
                            <li><Link to="/support">Get Support</Link></li>
                        </ul>
                    </div>
                </div>

                <div className="dashboard-insights">
                    <h2>Tax Insights for You</h2>
                    <p>Based on your profile, consider reviewing your investments for potential tax savings.</p>
                    <button className="dashboard-action-button">Explore Insights</button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
