// src/components/LoginForm/index.js

import React, {Component} from 'react'
import Cookies from 'js-cookie'
import {Redirect} from 'react-router-dom'
import Header from '../Header'; // Add Header here
import './index.css'; // Ensure CSS is imported

const websiteLogoInForm =
  'https://assets.ccbp.in/frontend/react-js/logo-img.png' // You can replace this with your WebsiteLogo.png

class LoginForm extends Component {
  state = {username: '', password: '', showSubmitError: false, errorMsg: ''}

  onGetUsername = event => this.setState({username: event.target.value})

  onGetPassword = event => this.setState({password: event.target.value})

  onSubmitSuccess = jwtToken => {
    const {history} = this.props

    Cookies.set('jwt_token', jwtToken, {expires: 30, path: '/'})

    history.replace('/')
  }

  onSubmitFailure = errorMsg => {
    this.setState({showSubmitError: true, errorMsg})
  }

  onSubmitLoginForm = async event => {
    event.preventDefault()
    const {username, password} = this.state
    const userDetails = {username, password}
    const loginApiUrl = 'http://127.0.0.1:5000/api/login' 
    const options = {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
        },
      body: JSON.stringify(userDetails),
    }
    const response = await fetch(loginApiUrl, options)
    const data = await response.json()
    if (response.ok === true) {
      this.onSubmitSuccess(data.jwt_token)
    } else {
      this.onSubmitFailure(data.error_msg)
    }
  }

  render() {
    const {username, password, showSubmitError, errorMsg} = this.state
    const jwtToken = Cookies.get('jwt_token')
    if (jwtToken !== undefined) {
      return <Redirect to="/" />
    }
    return (
      <>
        <Header />
        <div className="login-container">
          <form
            className="login-form-container"
            onSubmit={this.onSubmitLoginForm}
          >
            <div className="form-logo-container">
              <img src={websiteLogoInForm} alt="website logo" />
              <span className="logo-text">Garuda AI</span>
            </div>
            <label className="form-label" htmlFor="username">
              USERNAME
            </label>
            <br />
            <input
              className="form-input"
              type="text"
              value={username}
              onChange={this.onGetUsername}
              placeholder="username"
              id="username"
            />
            <br />
            <br />
            <label className="form-label" htmlFor="password">
              PASSWORD
            </label>
            <br />
            <input
              className="form-input"
              type="password"
              value={password}
              onChange={this.onGetPassword}
              placeholder="password"
              id="password"
            />
            <br />
            <br />
            <button className="form-submit-button" type="submit">
              Login
            </button>
            {showSubmitError && <p className="error-message">*{errorMsg}</p>}
          </form>
        </div>
      </>
    )
  }
}

export default LoginForm
