// src/components/TaxUploader/index.js

import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import Header from '../Header'; // Import Header
import './index.css';

const TaxUploader = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [uploadResult, setUploadResult] = useState(null);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState('');
  const [saveMessageType, setSaveMessageType] = useState('');
  const [suggestionsResult, setSuggestionsResult] = useState(null);


  const handleFileChange = (event) => {
    setSelectedFiles(Array.from(event.target.files));
    setMessage('');
    setMessageType('');
    setUploadResult(null);
    setSaveMessage('');
    setSaveMessageType('');
    setSuggestionsResult(null); // Clear suggestions on new file selection
  };


  const handleSubmit = async (event) => {
    event.preventDefault();
    if (selectedFiles.length === 0) {
      setMessage('Please select at least one file.');
      setMessageType('error');
      return;
    }

    setLoading(true);
    setMessage('Uploading and processing documents with AI...');
    setMessageType('info');
    setUploadResult(null);
    setSaveMessage('');
    setSaveMessageType('');
    setSuggestionsResult(null);

    const formData = new FormData();
    selectedFiles.forEach(file => {
      formData.append('documents', file);
    });

    const jwt_token = Cookies.get('jwt_token');

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      };
      if (jwt_token) {
        config.headers['Authorization'] = `Bearer ${jwt_token}`;
      }

      const response = await axios.post('http://127.0.0.1:5000/api/process_documents', formData, config);

      if (response.data.status === 'success' || response.data.status === 'partial_success') {
        setMessage(response.data.message || 'Documents processed and tax computed successfully!');
        setMessageType('success');
        setUploadResult(response.data);
        // Automatically fetch suggestions after successful processing
        handleGetSuggestions(response.data.aggregated_financial_data, response.data.final_tax_computation_summary);
      } else {
        setMessage(response.data.message || 'An unknown error occurred during processing.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Upload error:', error);
      if (error.response) {
          if (error.response.status === 401) {
              setMessage('Authentication required. Please log in.');
              setMessageType('error');
          } else if (error.response.data && error.response.data.error) {
              setMessage(`Error: ${error.response.data.error}`);
          } else {
              setMessage(`Server responded with status ${error.response.status}`);
          }
      } else {
          setMessage('An error occurred while uploading or processing documents. Check network connection and backend server.');
      }
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!uploadResult) {
      setSaveMessage('No data to save. Please process documents first.');
      setSaveMessageType('error');
      return;
    }

    setSaving(true);
    setSaveMessage('Saving extracted data and document paths...');
    setSaveMessageType('info');

    const jwt_token = Cookies.get('jwt_token');

    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
        }
      };
      if (jwt_token) {
        config.headers['Authorization'] = `Bearer ${jwt_token}`;
      }

      // Include suggestions from the current session if available
      const dataToSave = {
          ...uploadResult,
          suggestions_from_gemini: suggestionsResult ? suggestionsResult.suggestions_from_gemini : [],
          ml_prediction_summary: suggestionsResult ? {
              predicted_tax_liability: suggestionsResult.predicted_tax_liability,
              predicted_refund_due: suggestionsResult.predicted_refund_due,
              predicted_additional_due: suggestionsResult.predicted_additional_due,
          } : null
      };

      const response = await axios.post('http://127.0.0.1:5000/api/save_extracted_data', dataToSave, config);

      if (response.data.status === 'success') {
        setSaveMessage('Data saved successfully!');
        setSaveMessageType('success');
        setSelectedFiles([]);
        document.getElementById('file-input').value = '';
        setUploadResult(null); // Clear previous results after saving
        setSuggestionsResult(null); // Clear suggestions too
      } else {
        setSaveMessage(response.data.message || 'An unknown error occurred during saving.');
        setSaveMessageType('error');
      }
    } catch (error) {
      console.error('Save error:', error);
      if (error.response) {
        setSaveMessage(`Error saving data: ${error.response.data.message || error.response.status}`);
      } else {
        setSaveMessage('Network error while saving data.');
      }
      setSaveMessageType('error');
    } finally {
      setSaving(false);
    }
  };

  const handleGetSuggestions = async (financialData, taxSummary) => {
    setLoading(true); // Still show loading while suggestions are generated
    setMessage('Generating AI-powered suggestions...');
    setMessageType('info');
    setSuggestionsResult(null); // Clear previous suggestions

    const jwt_token = Cookies.get('jwt_token');

    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
        }
      };
      if (jwt_token) {
        config.headers['Authorization'] = `Bearer ${jwt_token}`;
      }

      const payload = {
        current_financial_data: financialData,
        tax_computation_summary: taxSummary
      };

      const response = await axios.post('http://127.0.0.1:5000/api/get_suggestions', payload, config);

      if (response.data.status === 'success') {
        setMessage('AI suggestions generated.');
        setMessageType('success');
        setSuggestionsResult(response.data);
      } else {
        setMessage(response.data.message || 'Failed to get suggestions.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Suggestions error:', error);
      setMessage('Error fetching AI suggestions. Check backend connection and API keys.');
      setMessageType('error');
    } finally {
      setLoading(false); // End loading after suggestions
    }
  };


  return (
    <>
      <Header />
      <div className="tax-uploader-container">
        <h2 className="tax-uploader-title">Upload Tax Documents</h2>
        <form onSubmit={handleSubmit}>
          <div className="tax-uploader-form-group">
            <label htmlFor="file-input" className="tax-uploader-label">Choose Files (select multiple with Ctrl/Cmd+Click):</label>
            <input
              id="file-input"
              type="file"
              multiple
              onChange={handleFileChange}
              className="tax-uploader-file-input"
              disabled={loading}
              accept=".pdf,.png,.jpg,.jpeg" // Specify accepted file types
            />
            {selectedFiles.length > 0 && (
              <p className="text-gray-600 mt-2">
                Selected: {selectedFiles.map(file => file.name).join(', ')}
              </p>
            )}
          </div>

          <button
            type="submit"
            className="tax-uploader-button"
            disabled={loading || selectedFiles.length === 0}
          >
            {loading ? 'Processing with AI...' : 'Upload & Compute Tax with AI'}
          </button>
        </form>

        {loading && (
          <div className="tax-uploader-loading">
            <div className="tax-uploader-spinner"></div>
            {message}
          </div>
        )}

        {message && !loading && (
          <div className={`tax-uploader-message ${messageType}`}>
            {message}
          </div>
        )}

        {/* Action buttons appear only after initial processing */}
        {uploadResult && !loading && (
          <div className="action-buttons-container">
              <button
                  type="button"
                  onClick={handleSave}
                  className="tax-uploader-button save-button"
                  disabled={saving}
              >
                  {saving ? 'Saving...' : 'Proceed & Save Data'}
              </button>
          </div>
        )}

        {saveMessage && !saving && (
          <div className={`tax-uploader-message ${saveMessageType}`}>
            {saveMessage}
          </div>
        )}

        {/* Display Suggestions from Gemini */}
        {suggestionsResult && !loading && (
            <div className="suggestions-output tax-summary-output">
                <h3 className="tax-uploader-title" style={{ marginTop: '30px', fontSize: '1.8em' }}>AI Recommendations</h3>
                <div className="section-box">
                    {suggestionsResult.suggestions_from_gemini && suggestionsResult.suggestions_from_gemini.length > 0 ? (
                        <>
                            <h4>Based on Gemini AI:</h4>
                            <ul className="suggestions-list">
                                {suggestionsResult.suggestions_from_gemini.map((suggestion, index) => (
                                    <li key={index}>{suggestion}</li>
                                ))}
                            </ul>
                        </>
                    ) : (
                        <p>Gemini did not provide specific suggestions at this moment, but your tax situation seems optimized.</p>
                    )}

                    {suggestionsResult.ml_prediction_summary && (
                        <>
                            <h4>ML Model Prediction:</h4>
                            {suggestionsResult.ml_prediction_summary.predicted_tax_liability !== undefined && (
                                <p><strong>Predicted Tax Payable:</strong> {suggestionsResult.ml_prediction_summary.predicted_tax_liability.toLocaleString()}</p>
                            )}
                            {suggestionsResult.ml_prediction_summary.predicted_refund_due !== undefined && (
                                <p className="refund-amount"><strong>Predicted Refund Due:</strong> {suggestionsResult.ml_prediction_summary.predicted_refund_due.toLocaleString()}</p>
                            )}
                            {suggestionsResult.ml_prediction_summary.predicted_additional_due !== undefined && (
                                <p className="tax-due-amount"><strong>Predicted Additional Tax Due:</strong> {suggestionsResult.ml_prediction_summary.predicted_additional_due.toLocaleString()}</p>
                            )}
                        </>
                    )}
                </div>
            </div>
        )}


        {/* Display Computation Results */}
        {uploadResult && (
          <div className="tax-summary-output">
            <h3 className="tax-uploader-title" style={{ marginTop: '30px', fontSize: '1.8em' }}>Computation Results</h3>

            {uploadResult.document_processing_summary && (
              <div className="document-processing-summary-section section-box">
                <h4>Document Processing Summary:</h4>
                {uploadResult.document_processing_summary.map((doc, index) => {
                  const statusClass = doc.status === 'success' ? 'status-success' :
                                         doc.status === 'warning' ? 'status-warning' : 'status-error';
                  return (
                    <div key={index} className="document-status-item">
                      <p><strong>File:</strong> {doc.filename} (<a href={`http://127.0.0.1:5000${doc.stored_path}`} target="_blank" rel="noopener noreferrer">View Stored Document</a>)</p>
                      <p><strong>Status:</strong> <span className={statusClass}>{doc.status.toUpperCase()}</span></p>
                      <p><strong>Identified Type:</strong> {doc.identified_type}</p>
                      <p><strong>Message:</strong> {doc.message || 'N/A'}</p>
                      {doc.extracted_fields && Object.keys(doc.extracted_fields).length > 0 && (
                        <p><strong>Extracted Fields (Partial):</strong>
                          <pre className="extracted-fields-preview">
                            {JSON.stringify(doc.extracted_fields, null, 2)}
                          </pre>
                        </p>
                      )}
                      {doc.extracted_raw_text && (
                          <p><strong>Extracted Raw Text Snippet:</strong> <pre className="extracted-fields-preview">{doc.extracted_raw_text.substring(0, 200)}...</pre></p>
                      )}
                    </div>
                  );
                })}
              </div>
            )}

            {uploadResult.aggregated_financial_data && (
              <div className="aggregated-financial-data-section">
                <h4 className="section-title">Aggregated Financial Data Summary</h4>

                <div className="income-details-section section-box">
                  <h5>Income Details</h5>
                  <p><strong>Financial Year:</strong> {uploadResult.aggregated_financial_data['Financial Year'] || 'Not Found'}</p>
                  <p><strong>Name:</strong> {uploadResult.aggregated_financial_data.Name || 'Not Found'}</p>
                  <p><strong>PAN:</strong> {uploadResult.aggregated_financial_data.PAN || 'Not Found'}</p>
                  <p><strong>Date of Birth:</strong> {uploadResult.aggregated_financial_data['Date of Birth'] || 'Not Found'}</p>
                  <p><strong>Age:</strong> {uploadResult.aggregated_financial_data.Age > 0 ? uploadResult.aggregated_financial_data.Age : 'Not Found'}</p>
                  <p><strong>Gross Annual Salary:</strong> {uploadResult.aggregated_financial_data['Gross Annual Salary']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Basic Salary:</strong> {uploadResult.aggregated_financial_data['Basic Salary']?.toLocaleString() || '0.00'}</p>
                  <p><strong>HRA Received:</strong> {uploadResult.aggregated_financial_data['HRA Received']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Total Exempt Allowances Claimed:</strong> {uploadResult.aggregated_financial_data['Total Exempt Allowances Claimed']?.toLocaleString() || '0.00'}</p>
                </div>

                <div className="deductions-section section-box">
                  <h5>Deductions Claimed</h5>
                  <p><strong>Standard Deduction Claimed:</strong> {uploadResult.aggregated_financial_data['Standard Deduction Claimed']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Professional Tax:</strong> {uploadResult.aggregated_financial_data['Professional Tax']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Interest on Home Loan Deduction (Section 24(b)):</strong> {uploadResult.aggregated_financial_data['Interest on Home Loan Deduction (Section 24(b))]']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Section 80C Investments Claimed:</strong> {uploadResult.aggregated_financial_data['Section 80C Investments Claimed']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Section 80D (Health Insurance Premiums) Claimed:</strong> {uploadResult.aggregated_financial_data['Section 80D (Health Insurance Premiums) Claimed']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Section 80E (Education Loan Interest) Claimed:</strong> {uploadResult.aggregated_financial_data['Section 80E (Education Loan Interest) Claimed']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Other Deductions (80CCD, 80G, etc.) Claimed:</strong> {uploadResult.aggregated_financial_data['Other Deductions (80CCD, 80G, etc.) Claimed']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Rent Paid (for HRA Exemption):</strong> {uploadResult.aggregated_financial_data['Rent Paid']?.toLocaleString() || '0.00'}</p>
                </div>

                <div className="taxation-summary-section section-box">
                  <h5>Taxation Overview</h5>
                  <p><strong>Net Taxable Income (Aggregated):</strong> {uploadResult.aggregated_financial_data['Net Taxable Income']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Final Tax Payable (Aggregated):</strong> {uploadResult.aggregated_financial_data['Final Tax Payable']?.toLocaleString() || '0.00'}</p>
                  <p><strong>TDS Deducted (Aggregated):</strong> {uploadResult.aggregated_financial_data['TDS Deducted']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Advance Tax Paid (Aggregated):</strong> {uploadResult.aggregated_financial_data['Advance Tax Paid']?.toLocaleString() || '0.00'}</p>
                  <p><strong>Tax Regime Chosen (from docs):</strong> <span className="highlight-regime">{uploadResult.aggregated_financial_data['Tax Regime Chosen'] || 'Not Specified'}</span></p>
                </div>
              </div>
            )}

            {uploadResult.final_tax_computation_summary && (
              <div className="final-tax-computation-section section-box">
                <h4 className="section-title">Final Tax Computation Summary (Rule-Based)</h4>
                
                <div className="computation-detail">
                  <p><strong>Calculated Gross Income:</strong> {uploadResult.final_tax_computation_summary.calculated_gross_income?.toLocaleString() || '0.00'}</p>
                  <p><strong>Calculated Total Deductions:</strong> {uploadResult.final_tax_computation_summary.calculated_total_deductions?.toLocaleString() || '0.00'}</p>
                  <p className="explanation">
                    (This typically includes deductions like 80C, 80D, Standard Deduction, Professional Tax etc.)
                  </p>
                </div>

                <div className="computation-detail">
                  <p><strong>Computed Taxable Income:</strong> {uploadResult.final_tax_computation_summary.computed_taxable_income?.toLocaleString() || '0.00'}</p>
                  <p className="explanation">
                    (Computed Taxable Income = Calculated Gross Income - Calculated Total Deductions and Exemptions)
                  </p>
                </div>
                
                <div className="computation-detail">
                  <p><strong>Estimated Tax Payable:</strong> {uploadResult.final_tax_computation_summary.estimated_tax_payable?.toLocaleString() || '0.00'}</p>
                  <p className="explanation">
                    (This is the tax calculated on your Computed Taxable Income, based on applicable tax slabs and regime.)
                  </p>
                </div>

                <div className="computation-detail">
                  <p><strong>Total TDS Credit:</strong> {uploadResult.final_tax_computation_summary.total_tds_credit?.toLocaleString() || '0.00'}</p>
                  <p className="explanation">
                    (TDS is Tax Deducted at Source, already paid on your behalf.)
                  </p>
                </div>

                <div className="computation-detail">
                  <p><strong>Tax Regime Considered:</strong> <span className="highlight-regime">{uploadResult.final_tax_computation_summary.regime_consideration || 'Not Specified'}</span></p>
                  <p className="explanation">
                    (The tax computation is typically done either under the Old Tax Regime or the New Tax Regime, chosen based on your declarations or optimal calculation.)
                  </p>
                </div>

                <div className="final-amount-box">
                  {uploadResult.final_tax_computation_summary.refund_due_from_tax > 0 && (
                    <p className="refund-amount">
                      <strong>Refund Due from Tax:</strong> {uploadResult.final_tax_computation_summary.refund_due_from_tax?.toLocaleString() || '0.00'}
                      <span className="explanation">(You may be eligible for a refund.)</span>
                    </p>
                  )}
                  {uploadResult.final_tax_computation_summary.tax_due_to_government > 0 && (
                    <p className="tax-due-amount">
                      <strong>Tax Due to Government:</strong> {uploadResult.final_tax_computation_summary.tax_due_to_government?.toLocaleString() || '0.00'}
                      <span className="explanation">(You may need to pay this amount.)</span>
                    </p>
                  )}
                </div>

                {uploadResult.final_tax_computation_summary.notes && (
                  <p className="computation-notes">
                    **Note:** {uploadResult.final_tax_computation_summary.notes}
                  </p>
                )}
              </div>
            )}

            <div className="timestamp-user-id-section">
              {uploadResult.timestamp && (
                <p><strong>Timestamp:</strong> {new Date(uploadResult.timestamp).toLocaleString()}</p>
              )}
              {uploadResult.user_id && (
                <p><strong>User ID:</strong> {uploadResult.user_id}</p>
              )}
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default TaxUploader;
