import React, { useState } from 'react';
import Header from '../Header'; // Assuming src/components/Header/index.js
import TaxUploader from './TaxUploader'; // Corrected path: assuming src/components/TaxUploader/index.js
import TaxCalculator from './TaxCalculator'; // Corrected path: assuming src/components/TaxCalculator/index.js
import TaxHistory from './TaxHistory';     // Corrected path: assuming src/components/TaxHistory/index.js
import ContactUs from './ContactUs';       // Corrected path: assuming src/components/ContactUs/index.js
import About from './About';               // NEW: Import About component
import Cookies from 'js-cookie'; // Import Cookies for logout

import '../index.css'; // Assuming src/index.css for general styling

const Dashboard = ({ onLogout }) => {
  // State to manage which content is currently displayed
  const [activeComponent, setActiveComponent] = useState('upload'); // Default to 'upload'

  const handleNavigation = (componentName) => {
    setActiveComponent(componentName);
  };

  const handleLogoutClick = () => {
    onLogout(); // Call the logout function passed from App.js
  };

  return (
    <div className="dashboard-page">
      {/* Pass handleNavigation to Header so it can control which component is active */}
      <Header onLogout={handleLogoutClick} onNavigate={handleNavigation} /> 
      
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h1>Welcome to GarudaTax AI!</h1>
          <p>Your intelligent assistant for seamless tax filing.</p>
        </div>

        <div className="dashboard-actions">
          {/* Navigation Buttons */}
          <button 
            className={`dashboard-action-button ${activeComponent === 'upload' ? 'active' : ''}`} 
            onClick={() => handleNavigation('upload')}
          >
            <i className="fas fa-upload mr-2"></i> Start New Filing
          </button>
          <button 
            className={`dashboard-action-button ${activeComponent === 'calculator' ? 'active' : ''}`} 
            onClick={() => handleNavigation('calculator')}
          >
            <i className="fas fa-calculator mr-2"></i> Tax Calculator
          </button>
          <button 
            className={`dashboard-action-button ${activeComponent === 'history' ? 'active' : ''}`} 
            onClick={() => handleNavigation('history')}
          >
            <i className="fas fa-history mr-2"></i> View Records
          </button>
          <button 
            className={`dashboard-action-button ${activeComponent === 'support' ? 'active' : ''}`} 
            onClick={() => handleNavigation('support')}
          >
            <i className="fas fa-life-ring mr-2"></i> Get Support
          </button>
          {/* Explore Insights is integrated into TaxUploader's results for now */}
          <button 
            className={`dashboard-action-button ${activeComponent === 'insights' ? 'active' : ''}`} 
            onClick={() => handleNavigation('upload')} // For now, go to upload which shows insights
          >
            <i className="fas fa-lightbulb mr-2"></i> Explore Insights
          </button>
        </div>

        {/* Render active component based on state */}
        <div className="dashboard-content">
          {activeComponent === 'upload' && <TaxUploader />}
          {activeComponent === 'calculator' && <TaxCalculator />}
          {activeComponent === 'history' && <TaxHistory />}
          {activeComponent === 'support' && <ContactUs />}
          {activeComponent === 'about' && <About />} {/* NEW: Render About component */}
        </div>

        <div className="dashboard-insights">
          <h2>Smart Insights & Predictions</h2>
          <p>Leverage AI to understand your tax situation better and get personalized recommendations.</p>
          <p>Upload your documents to get started!</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
