// src/components/History/index.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import Header from '../Header';

const History = () => {
  const [historyData, setHistoryData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHistory = async () => {
      setLoading(true);
      setError('');
      try {
        const jwt_token = Cookies.get('jwt_token');
        if (!jwt_token) {
          setError('Authentication required. Please log in.');
          setLoading(false);
          return;
        }

        const config = {
          headers: {
            'Authorization': `Bearer ${jwt_token}`,
          }
        };
        const response = await axios.get('http://127.0.0.1:5000/api/history', config);
        
        if (response.data.status === 'success') {
          setHistoryData(response.data.history);
        } else {
          setError(response.data.message || 'Failed to fetch history.');
        }
      } catch (err) {
        console.error('History fetch error:', err);
        if (err.response && err.response.status === 401) {
            setError('Session expired or unauthorized. Please log in again.');
        } else {
            setError('An error occurred while fetching history. Please try again later.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, []); // Empty dependency array means this runs once on mount

  if (loading) {
    return (
      <>
        <Header />
        <div className="history-container">
          <p>Loading history...</p>
        </div>
      </>
    );
  }

  if (error) {
    return (
      <>
        <Header />
        <div className="history-container">
          <p className="error-message">{error}</p>
        </div>
      </>
    );
  }

  return (
    <>
      <Header />
      <div className="history-container">
        <h2 className="history-title">Your Tax Filing History</h2>
        {historyData.length === 0 ? (
          <p>No tax filing history found. Start by uploading documents on the "Start Now" page!</p>
        ) : (
          <div className="history-list">
            {historyData.map((record, index) => (
              <div key={index} className="history-item">
                <h3>Record {historyData.length - index} - {new Date(record.timestamp).toLocaleString()}</h3>
                
                <div className="history-section">
                    <h4>Aggregated Financial Data:</h4>
                    <p><strong>Gross Annual Salary:</strong> ₹{record.aggregated_financial_data?.['Gross Annual Salary']?.toLocaleString() || '0.00'}</p>
                    <p><strong>Total Deductions Claimed (80C, 80D etc.):</strong> ₹{(record.aggregated_financial_data?.['Section 80C Investments Claimed'] + record.aggregated_financial_data?.['Section 80D (Health Insurance Premiums) Claimed'] + record.aggregated_financial_data?.['Standard Deduction Claimed'])?.toLocaleString() || '0.00'}</p>
                    <p><strong>Net Taxable Income:</strong> ₹{record.aggregated_financial_data?.['Net Taxable Income']?.toLocaleString() || '0.00'}</p>
                </div>

                <div className="history-section">
                    <h4>Tax Computation Summary:</h4>
                    <p><strong>Estimated Tax Payable:</strong> ₹{record.final_tax_computation_summary?.estimated_tax_payable?.toLocaleString() || '0.00'}</p>
                    {record.final_tax_computation_summary?.refund_due_from_tax > 0 && (
                        <p className="refund-amount"><strong>Refund Due:</strong> ₹{record.final_tax_computation_summary?.refund_due_from_tax?.toLocaleString()}</p>
                    )}
                    {record.final_tax_computation_summary?.tax_due_to_government > 0 && (
                        <p className="tax-due-amount"><strong>Tax Due:</strong> ₹{record.final_tax_computation_summary?.tax_due_to_government?.toLocaleString()}</p>
                    )}
                </div>

                {record.suggestions_from_gemini && record.suggestions_from_gemini.length > 0 && (
                    <div className="history-section">
                        <h4>AI Suggestions (from Gemini):</h4>
                        <ul className="suggestions-list">
                            {record.suggestions_from_gemini.map((sugg, i) => (
                                <li key={i}>{sugg}</li>
                            ))}
                        </ul>
                    </div>
                )}

                {record.ml_prediction_summary && (
                    <div className="history-section">
                        <h4>ML Prediction:</h4>
                        {record.ml_prediction_summary.predicted_tax_liability !== undefined && (
                            <p><strong>Predicted Tax Payable:</strong> ₹{record.ml_prediction_summary.predicted_tax_liability.toLocaleString()}</p>
                        )}
                        {record.ml_prediction_summary.predicted_refund_due !== undefined && (
                            <p className="refund-amount"><strong>Predicted Refund Due:</strong> ₹{record.ml_prediction_summary.predicted_refund_due.toLocaleString()}</p>
                        )}
                        {record.ml_prediction_summary.predicted_additional_due !== undefined && (
                            <p className="tax-due-amount"><strong>Predicted Additional Tax Due:</strong> ₹{record.ml_prediction_summary.predicted_additional_due.toLocaleString()}</p>
                        )}
                    </div>
                )}

                {record.document_processing_summary && record.document_processing_summary.length > 0 && (
                    <div className="history-section">
                        <h4>Uploaded Documents:</h4>
                        <ul className="uploaded-docs-list">
                            {record.document_processing_summary.map((doc, i) => (
                                <li key={i}>
                                    {doc.filename} (<a href={`http://127.0.0.1:5000${doc.stored_path}`} target="_blank" rel="noopener noreferrer">View</a>)
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default History;
